var night_temp=msg.payload.night_temp;
var range=msg.payload.range;
var curr_temp=msg.payload.temp;
var heater=msg.payload.heater

// Период
if (time=="day"){
    if ((curr_temp < day_temp) && (heater=='off')){
        msg.payload="on"; // Включить обогреватель
        return msg;
    }
    if ((curr_temp > (day_temp + range)) && (heater=='on')){
        msg.payload="off"; // Выключить обогреватель
        return msg;
    }
}
if (time=="night"){
    if ((curr_temp < night_temp) && (heater=='off')){
        msg.payload="on"; // Включить обогреватель
        return msg;
    }
    if ((curr_temp > (night_temp + range)) && (heater=='on')){
        msg.payload="off"; // Выключить обогреватель
        return msg;
    }
}
msg.payload=null
return msg;

